import { useState, useEffect } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";
import style from "../SubjectComponent/Subject.module.css";
import baseUrl from "../../../baseUrl";

function Exam() {
  const [display, setDisplay] = useState({
    display: "none"
  });

  function handleAddExam() {
    setDisplay({ display: "block" });
  }

  function handleCloseExam() {
    setDisplay({ display: "none" });
  }

  const [exams, setExams] = useState([]);

  useEffect(() => {
    async function getAllExam() {
      try {
        const response = await axios.get(`${baseUrl}/exam`);
        setExams(response.data);
      } catch (error) {
        console.error(error);
      }
    }
    getAllExam();
  }, []);

  const [exam, setExam] = useState({
    exam_date: "",
    exam_desc: "",
    exam_level: "",
    exam_marks: 10,
    exam_pass_marks: 5,
    exam_total_question: "",
    name_subject_name: "",
    total_time: ""
  });

  function handleInput(e) {
    setExam({
      ...exam,
      [e.target.name]: e.target.value
    });
  }

  async function handleAddNewExam() {
    try {
      await axios.post(`${baseUrl}/exam`, exam);
      setStatus(true);
    } catch (error) {
      console.error(error);
    }
  }

  const [status, setStatus] = useState(false);

  const [questions, setQuestions] = useState([]);

  useEffect(() => {
    async function getAllQuestions() {
      try {
        const response = await axios.get(`${baseUrl}/question`);
        setQuestions(response.data);
      } catch (error) {
        console.error(error);
      }
    }
    getAllQuestions();
  }, []);

  const [statusDeleteExam, setStatusDeleteExam] = useState(false);

  async function deleteExam(id) {
    try {
      for (let i = 0; i < questions.length; i++) {
        if (parseInt(questions[i].exam_id) === parseInt(id)) {
          await axios.delete(`${baseUrl}/question/${questions[i].id}`);
        }
      }
      await axios.delete(`${baseUrl}/exam/${id}`);
      setStatusDeleteExam(true);
    } catch (error) {
      console.error(error);
    }
  }

  if (status) return <Exam />;

  if (statusDeleteExam) return <Exam />;

  return (
    <>
      <div id={style.displayHeadingBox}>
        <h2>Exam List</h2>
      </div>

      <div id={style.tableBox}>
        <table>
          <thead>
            <tr>
              <th id={style.center}>Exam Name</th>
              <th id={style.center}>Exam Desc.</th>
              <th id={style.center}>Exam Creation Date</th>
              <th id={style.center}>Exam Level</th>
              <th id={style.center}>Options</th>
            </tr>
          </thead>
          <tbody id={style.tbody}>
            {exams.map((data, i) => (
              <tr key={i}>
                <td>{data.name_subject_name}</td>
                <td>{data.exam_desc}</td>
                <td>{data.exam_date}</td>
                <td>{data.exam_level}</td>
                <td>
                  <NavLink exact to={`/AdminDashboard/Exam/Details/${data.id}`}>
                    <button>Details</button>
                  </NavLink>

                  <NavLink exact to={`/AdminDashboard/Exam/ViewQuestion/${data.id}`}>
                    <button>View Question</button>
                  </NavLink>

                  <NavLink exact to={`/AdminDashboard/Exam/AddQuestion/${data.id}`}>
                    <button>Add Question</button>
                  </NavLink>

                  <button onClick={() => deleteExam(data.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div id={style.addSubjectBox}>
        <button onClick={handleAddExam}>Add Exam</button>
      </div>

      <div id={style.addBox} style={display}>
        <label htmlFor="nameField">Enter Subject Name</label>
        <input id="nameField" onChange={(e) => handleInput(e)} name="name_subject_name" type="text" placeholder="Enter Subject Name" />

        <label htmlFor="descField">Enter Exam desc</label>
        <input id="descField" onChange={(e) => handleInput(e)} name="exam_desc" type="text" placeholder="Enter Exam desc" />

        <label htmlFor="levelField">Enter Exam Level</label>
        <input id="levelField" onChange={(e) => handleInput(e)} name="exam_level" type="text" placeholder="Enter Exam Level" />

        <label htmlFor="totalQuestionField">Enter Total Question</label>
        <input id="totalQuestionField" onChange={(e) => handleInput(e)} name="exam_total_question" type="text" placeholder="Enter Total Question" />

        <label htmlFor="timeField">Enter Time</label>
        <input id="timeField" onChange={(e) => handleInput(e)} name="total_time" type="text" placeholder="Enter Time" />

        <div id={style.buttonBox}>
          <button onClick={handleAddNewExam}>Add</button>
          <button onClick={handleCloseExam}>Close</button>
        </div>
      </div>
    </>
  );
}

export default Exam;
